export const initialState = {
    isLoggingIn: false, //로그인 시도중 
    isLoggingOut: false,
    isLoggedIn: false,
    me: null,
    signUpData: {},
    loginData: {},
}

export const loginRequestAction = (data) => {
    return {
        type: 'LOG_IN_REQUEST',
        data,
    }
}

// log-out
export const logoutRequestAction = () => {
    return {
        type: 'LOG_OUT_REQUEST',
    }
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'LOG_IN_REQUEST':
            console.log('reducer logIn')
            return {
                ...state,
                isLoggingIn: false,
            }

        case 'LOG_IN_SUCESS':
            return {
                ...state,
                isLoggingIn: false,
                isLoggedIn: true,
                me: { ...action.data, nickname: '오규찬' },
            }

        case 'LOG_IN_FAILURE':
            return {
                ...state,
                isLoggingIn: false,
                isLoggedIn: false,

            }

        case 'LOG_OUT_REQUEST':
            return {
                ...state,
                isLoggingOut: true,
            }

        case 'LOG_OUT_SUCESS':
            return {
                ...state,
                isLoggingOut: false,
                isLoggedIn: false,
                me: null,
            }

        case 'LOG_OUT_FAILURE':
            return {
                ...state,
                isLoggingOut: false,
            }

        default:
            return state
    }
}

export default reducer
